    public void calc()
    {
        try
        {
            int total=0;
            Connection con = DBConnection.getConnection();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("select count(*) as rowcount from complaint c,status s where " +
                    "c.receipt_no=s.receipt_no and assigned_to='"+jTextField1.getText()+"'");
            while(rs.next())
            {
                total=rs.getInt("rowcount");

            }
            int comp=0;
            rs=st.executeQuery("select count(*) as rowcount from complaint c,status s where " +
                    "c.receipt_no=s.receipt_no and assigned_to='"+jTextField1.getText()+"' and c.receipt_no in " +
                    "(select receipt_no from status where status='Completed')");
            while(rs.next())
            {
                comp = rs.getInt("rowcount");
            }
           float per = ((float)comp/(float)total)*100;
           int per1 = (int)per;
           Integer percent = per1;
            jLabel2.setText("Success Rate is "+percent.toString()+"%");
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }
    }
